package exemplos;

public class Programa04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int soma ;
		int num1 = 5;
		int num2 = 3;
        soma = 5 + 3;
        
        
		System.out.printf("A soma do %s e %s é: %s%n",num1,num2,soma);
		
		int produto = num1 * num2;
		
		System.out.printf("A multiplicação do %s e %s é: %s%n",num1,num2,produto);

		

		int num3 = 10;
		int num4 = 2;
		int divisao = num3/num4;
		
		System.out.printf("A divisão do %s e %s é: %s%n",num3,num4,divisao);
		
		num4 = 3;
		
		double divisao2 = (double) num3/num4;
		
		System.out.printf("A divisão do %s e %s é: %s%n",num3,num4,divisao2);
		
		
		int resto = num3%num4;
		
		System.out.printf("O resto do %s e %s é: %s%n",num3,num4,resto);
		
		
		int num5 = 20;
		
		System.out.println(num5);
		
		num5++;
		System.out.println(num5);

		num5--;
		System.out.println(num5);
		
		

				

		
		
		
	}

}
